import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import Movie_list from './Components/Movie_list';
import moviesData from './Components/moviesData';
import Add_movie from './Components/Add_movie/Add_movie';


function App() {
  const [Movie_list,setMovie_list] = useState(moviesData);
    const Add_movie = (e,newMovie) => {
      e.preventDefault();
      return setMovie_list([...Movie_list,
        {title:"central intelligence",
          date:"2016",
          rate:4,
          description:"something about it ",
          poster:"moviePosters/centralintelligence.jpg"}
        ]);
    };

  return (
    <div>
    <Movie_list data={moviesData}>
    </Movie_list>
    <Add_movie AddMovie={Add_movie} />
</div>
  );
}

export default App;
