import React from 'react'

const Rate =({rating})=>
{ let starArray=[];
  let x=<span>★</span>;
  for(let i=0;i<rating;i++)
    {starArray.push(x)


      if(i>rating){
            x=<span>☆</span>
      }
    }
  return(
    starArray
  )
}
export default Rate ;
