import React from 'react'

export default  Rate =({rating})=>
{ let starArray=[]
  for(let i=0;i<rating;i++)
    { if (i<=rating){
          starArray.push(<span>★</span>)
    }else{          starArray.push(<span>☆</span>)
}
  return(
    starArray
  )
}
}
