import React from 'react'

const Rate =({rating,index})=>
{ let starArray=[];
  for(let i=0;i<5;i++){
      if(i<rating)
            starArray.push(
              <span className='star'
                style={{ cursor: 'pointer' }}
             onClick={() => index(i)}  >★</span>)
      else
              starArray.push(
                <span className='star'
                  style={{ cursor: 'pointer' }}
               onClick={() => index(i)}>☆</span>)
}
  return(
    starArray
  )
}
export default Rate ;
