import React from 'react'

const Rate =({rating,index})=>
{ const searchRating=(rating)=>
  {let starArray=[]
  for(let i=1;i<=5;i++)
    {if(i<=rating)
      {
      starArray.push(<span
        className="rating"
            key={i}
            style={{ cursor: 'pointer' }}
            onClick={() => index(i)}>★</span>)}
    else{      starArray.push(<span
      className="rating"
            key={i}
            style={{ cursor: 'pointer' }}
            onClick={() => index(i)}>☆</span>)}
}
  return(
    starArray
  )
}
return
<div>{searchRating(rating)}</div>;
}

export default Rate ;
